import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { getMovieById, getShowtimes } from '../services/api';

const MovieDetail = () => {
  const { movieId } = useParams();
  const [movie, setMovie] = useState(null);
  const [showtimes, setShowtimes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchMovieDetails = async () => {
      try {
        const [movieResponse, showtimesResponse] = await Promise.all([
          getMovieById(movieId),
          getShowtimes(movieId)
        ]);
        setMovie(movieResponse.data);
        setShowtimes(showtimesResponse.data);
        setLoading(false);
      } catch (err) {
        setError('Failed to fetch movie details');
        setLoading(false);
      }
    };

    fetchMovieDetails();
  }, [movieId]);

  if (loading) return <div className="text-center p-8">Loading...</div>;
  if (error) return <div className="text-center text-red-500 p-8">{error}</div>;
  if (!movie) return <div className="text-center p-8">Movie not found</div>;

  // Hàm định dạng thời gian hợp lệ
  const formatDateTime = (dateTimeStr) => {
    try {
      const date = new Date(dateTimeStr);
      if (isNaN(date.getTime())) {
        return 'Thời gian không hợp lệ';
      }
      return {
        date: date.toLocaleDateString(),
        time: date.toLocaleTimeString()
      };
    } catch (error) {
      return {
        date: 'Thời gian không hợp lệ',
        time: ''
      };
    }
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="flex flex-col md:flex-row gap-8">
        <div className="md:w-1/3">
          <img
            src={movie.poster_url}
            alt={movie.title}
            className="w-full rounded-lg shadow-lg"
          />
        </div>
        <div className="md:w-2/3">
          <h1 className="text-3xl font-bold mb-4">{movie.title}</h1>
          <p className="text-gray-600 mb-4">{movie.description}</p>
          <div className="mb-4">
            <h2 className="text-xl font-semibold mb-2">Showtimes</h2>
            <div className="grid grid-cols-2 md:grid-cols-3 gap-4">
              {showtimes.map((showtime) => {
                const formattedTime = formatDateTime(showtime.start_time);
                return (
                  <div
                    key={showtime._id}
                    className="border rounded p-4 hover:bg-gray-50 cursor-pointer"
                  >
                    <p className="font-semibold">{formattedTime.date}</p>
                    <p className="text-gray-600">{formattedTime.time}</p>
                    <button
                      onClick={() => window.location.href = `/booking/${showtime._id}`}
                      className="mt-2 w-full bg-blue-500 text-white py-2 rounded hover:bg-blue-600"
                    >
                      Book Now
                    </button>
                  </div>
                );
              })}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default MovieDetail;
