import React, { useState } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { useNotifications } from './NotificationProvider';
import NotificationDropdown from './NotificationDropdown';
import {
  HomeIcon,
  TicketIcon,
  UserCircleIcon,
  ArrowRightOnRectangleIcon,
  Bars3Icon,
  XMarkIcon,
  FilmIcon
} from '@heroicons/react/24/outline';
import { motion, AnimatePresence } from 'framer-motion';

const Navbar = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const customerId = localStorage.getItem('customer_id');
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  const handleLogout = () => {
    localStorage.removeItem('customer_id');
    navigate('/login');
  };

  const isActive = (path) => {
    return location.pathname === path;
  };

  const navLinks = customerId ? [
    { path: '/', label: 'Trang chủ', icon: HomeIcon },
    { path: '/bookings', label: 'Đặt vé của tôi', icon: TicketIcon },
    { path: '/profile', label: 'Tài khoản', icon: UserCircleIcon },
  ] : [];

  return (
    <motion.nav 
      initial={{ y: -100 }}
      animate={{ y: 0 }}
      className="fixed top-0 left-0 right-0 z-50 bg-gradient-to-r from-blue-600 via-blue-700 to-blue-800 text-white shadow-lg backdrop-blur-sm bg-opacity-90"
    >
      <div className="container mx-auto px-4">
        <div className="flex justify-between items-center h-16">
          {/* Logo */}
          <Link to="/" className="flex items-center space-x-3">
            <motion.div
              whileHover={{ scale: 1.1 }}
              whileTap={{ scale: 0.95 }}
              className="flex items-center justify-center w-10 h-10 bg-white text-blue-600 rounded-lg shadow-md"
            >
              <FilmIcon className="w-6 h-6" />
            </motion.div>
            <motion.span 
              initial={{ opacity: 0, x: -20 }}
              animate={{ opacity: 1, x: 0 }}
              className="hidden md:block text-xl font-bold"
            >
              Movie Booking
            </motion.span>
          </Link>

          {/* Desktop Navigation */}
          <div className="hidden md:flex items-center space-x-2">
            {navLinks.map(({ path, label, icon: Icon }) => (
              <motion.div
                key={path}
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                <Link
                  to={path}
                  className={`flex items-center px-4 py-2 rounded-lg transition-all duration-200 ${
                    isActive(path)
                      ? 'bg-white text-blue-600 shadow-md'
                      : 'hover:bg-white/10 text-blue-50'
                  }`}
                >
                  <Icon className="w-5 h-5 mr-2" />
                  {label}
                </Link>
              </motion.div>
            ))}

            {customerId && (
              <>
                <div className="px-2">
                  <NotificationDropdown />
                </div>
                <motion.button
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                  onClick={handleLogout}
                  className="flex items-center px-4 py-2 rounded-lg hover:bg-white/10 text-blue-50 transition-all duration-200"
                >
                  <ArrowRightOnRectangleIcon className="w-5 h-5 mr-2" />
                  Đăng xuất
                </motion.button>
              </>
            )}

            {!customerId && (
              <motion.div
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                <Link
                  to="/login"
                  className="flex items-center px-6 py-2 bg-white text-blue-600 rounded-lg font-medium hover:bg-opacity-90 transition-all duration-200 shadow-md"
                >
                  <ArrowRightOnRectangleIcon className="w-5 h-5 mr-2" />
                  Đăng nhập
                </Link>
              </motion.div>
            )}
          </div>

          {/* Mobile Menu Button */}
          <motion.button
            whileHover={{ scale: 1.1 }}
            whileTap={{ scale: 0.9 }}
            onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
            className="md:hidden p-2 rounded-lg hover:bg-white/10 transition-colors duration-200"
          >
            {isMobileMenuOpen ? (
              <XMarkIcon className="w-6 h-6" />
            ) : (
              <Bars3Icon className="w-6 h-6" />
            )}
          </motion.button>
        </div>

        {/* Mobile Navigation */}
        <AnimatePresence>
          {isMobileMenuOpen && (
            <motion.div
              initial={{ opacity: 0, height: 0 }}
              animate={{ opacity: 1, height: 'auto' }}
              exit={{ opacity: 0, height: 0 }}
              transition={{ duration: 0.2 }}
              className="md:hidden py-4 space-y-2 overflow-hidden"
            >
              {navLinks.map(({ path, label, icon: Icon }) => (
                <motion.div
                  key={path}
                  initial={{ x: -20, opacity: 0 }}
                  animate={{ x: 0, opacity: 1 }}
                  transition={{ duration: 0.2 }}
                >
                  <Link
                    to={path}
                    className={`flex items-center px-4 py-3 rounded-lg transition-all duration-200 ${
                      isActive(path)
                        ? 'bg-white text-blue-600 shadow-md'
                        : 'hover:bg-white/10 text-blue-50'
                    }`}
                    onClick={() => setIsMobileMenuOpen(false)}
                  >
                    <Icon className="w-5 h-5 mr-3" />
                    {label}
                  </Link>
                </motion.div>
              ))}

              {customerId && (
                <>
                  <motion.div
                    initial={{ x: -20, opacity: 0 }}
                    animate={{ x: 0, opacity: 1 }}
                    transition={{ duration: 0.2, delay: 0.1 }}
                    className="px-4 py-2"
                  >
                    <NotificationDropdown />
                  </motion.div>
                  <motion.button
                    initial={{ x: -20, opacity: 0 }}
                    animate={{ x: 0, opacity: 1 }}
                    transition={{ duration: 0.2, delay: 0.2 }}
                    onClick={() => {
                      handleLogout();
                      setIsMobileMenuOpen(false);
                    }}
                    className="flex items-center w-full px-4 py-3 rounded-lg hover:bg-white/10 text-blue-50 transition-all duration-200"
                  >
                    <ArrowRightOnRectangleIcon className="w-5 h-5 mr-3" />
                    Đăng xuất
                  </motion.button>
                </>
              )}

              {!customerId && (
                <motion.div
                  initial={{ x: -20, opacity: 0 }}
                  animate={{ x: 0, opacity: 1 }}
                  transition={{ duration: 0.2 }}
                >
                  <Link
                    to="/login"
                    className="flex items-center mx-4 px-4 py-3 bg-white text-blue-600 rounded-lg font-medium hover:bg-opacity-90 transition-all duration-200 shadow-md"
                    onClick={() => setIsMobileMenuOpen(false)}
                  >
                    <ArrowRightOnRectangleIcon className="w-5 h-5 mr-3" />
                    Đăng nhập
                  </Link>
                </motion.div>
              )}
            </motion.div>
          )}
        </AnimatePresence>
      </div>
    </motion.nav>
  );
};

export default Navbar;
