import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import { getBookingById } from '../services/api';

const BookingDetail = () => {
  const { bookingId } = useParams();
  const [booking, setBooking] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchBookingDetails = async () => {
      try {
        const response = await getBookingById(bookingId);
        setBooking(response.data);
        setLoading(false);
      } catch (err) {
        setError('Failed to fetch booking details');
        setLoading(false);
      }
    };

    fetchBookingDetails();
  }, [bookingId]);

  if (loading) return <div className="text-center p-8">Loading...</div>;
  if (error) return <div className="text-center text-red-500 p-8">{error}</div>;
  if (!booking) return <div className="text-center p-8">Booking not found</div>;

  return (
    <div className="min-h-screen bg-gray-100 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-3xl mx-auto bg-white shadow-xl rounded-lg overflow-hidden">
        <div className="bg-gradient-to-r from-blue-600 to-indigo-700 p-6">
          <h2 className="text-3xl font-bold text-white text-center">Chi tiết đặt vé</h2>
        </div>

        {loading ? (
          <div className="p-8 text-center">
            <p className="text-lg text-gray-600">Đang tải chi tiết đặt vé...</p>
            {/* Optional: Add a spinner or loading animation here */}
          </div>
        ) : booking ? (
          <div className="p-8 space-y-6">
            <div>
              <h3 className="text-xl font-semibold text-gray-800 mb-2">Thông tin vé</h3>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4 text-gray-700">
                <p><span className="font-medium">Mã đặt vé:</span> {booking._id}</p>
                <p><span className="font-medium">Trạng thái:</span> <span className={`px-2 py-1 text-xs font-semibold rounded-full ${getStatusColor(booking.status)}`}>{getDisplayStatus(booking.status)}</span></p>
                <p><span className="font-medium">Tên phim:</span> {booking.movie_id?.title || 'N/A'}</p>
                <p><span className="font-medium">Suất chiếu:</span> {new Date(booking.showtime).toLocaleString()}</p>
                <p className="md:col-span-2"><span className="font-medium">Ghế đã chọn:</span> {booking.seats.map(seat => seat.seat_number).join(', ')}</p>
                <p><span className="font-medium">Tổng tiền:</span> {booking.total_amount.toLocaleString('vi-VN')} VNĐ</p>
                <p><span className="font-medium">Ngày đặt:</span> {new Date(booking.created_at).toLocaleString()}</p>
              </div>
            </div>

            {booking.payment_status && (
              <div className="border-t pt-6">
                <h3 className="text-xl font-semibold text-gray-800 mb-2">Thông tin thanh toán</h3>
                <div className="text-gray-700">
                  <p><span className="font-medium">Trạng thái:</span> 
                    <span className={`px-2 py-1 text-xs font-semibold rounded-full ${booking.payment_status === 'succeeded' ? 'bg-green-100 text-green-700' : 'bg-yellow-100 text-yellow-700'}`}>
                      {booking.payment_status === 'succeeded' ? 'Thành công' : 'Đang chờ'}
                    </span>
                  </p>
                  {/* Hiển thị thêm thông tin thanh toán nếu cần */}
                </div>
              </div>
            )}

            {/* Phần hiển thị notification đã được xoá bỏ */}
            {/* {booking.notifications && booking.notifications.length > 0 && ( ... )} */}

          </div>
        ) : (
          <div className="p-8 text-center">
            {/* ... existing code ... */}
          </div>
        )}
      </div>
    </div>
  );
};

export default BookingDetail;
