import React, { createContext, useState, useEffect, useContext } from 'react';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { getNotifications } from '../services/api';

const NotificationContext = createContext();

export const useNotifications = () => useContext(NotificationContext);

export const NotificationProvider = ({ children }) => {
  const [notifications, setNotifications] = useState([]);
  const [loading, setLoading] = useState(true);
  const [connectionStatus, setConnectionStatus] = useState('disconnected');
  const [eventSource, setEventSource] = useState(null);

  // Lấy thông báo ban đầu từ API
  useEffect(() => {
    const customerId = localStorage.getItem('customer_id');
    if (customerId) {
      const fetchNotifications = async () => {
        try {
          const response = await getNotifications();
          console.log('Dữ liệu thông báo đã tải:', response.data);
          setNotifications(response.data || []);
        } catch (error) {
          console.error('Lỗi khi lấy thông báo:', error);
        } finally {
          setLoading(false);
        }
      };
      fetchNotifications();
    } else {
      setLoading(false);
    }
  }, []);

  // Kết nối SSE để nhận thông báo realtime
  useEffect(() => {
    const customerId = localStorage.getItem('customer_id');
    if (!customerId) {
      console.log('Không tìm thấy customer_id, không thể kết nối SSE');
      return;
    }

    console.log('Bắt đầu kết nối SSE...');
    
    const url = `http://localhost:8006/notifications/subscribe/${customerId}`;
    const sse = new EventSource(url);
    setEventSource(sse);
    
    // Xử lý kết nối thành công
    sse.addEventListener('connection_established', (event) => {
      const data = JSON.parse(event.data);
      console.log('Kết nối SSE thành công:', data);
      setConnectionStatus('connected');
    });
    
    // Xử lý thông báo chưa đọc ban đầu
    sse.addEventListener('unread_notifications', (event) => {
      const unreadNotifications = JSON.parse(event.data);
      console.log('Nhận thông báo chưa đọc:', unreadNotifications);
      
      if (unreadNotifications && unreadNotifications.length > 0) {
        setNotifications(prev => {
          // Kết hợp thông báo mới với danh sách hiện tại, tránh trùng lặp
          const existingIds = new Set(prev.map(n => n._id));
          const filteredNew = unreadNotifications.filter(n => !existingIds.has(n._id));
          return [...filteredNew, ...prev];
        });
      }
    });
    
    // Xử lý thông báo mới
    sse.addEventListener('new_notification', (event) => {
      const notification = JSON.parse(event.data);
      console.log('Nhận thông báo mới:', notification);
      
      // Hiển thị toast thông báo
      toast.success(notification.content, {
        position: "top-right",
        autoClose: 5000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
      });
      
      // Cập nhật danh sách thông báo
      setNotifications(prev => {
        // Kiểm tra nếu thông báo đã tồn tại
        const exists = prev.some(n => n._id === notification._id);
        if (exists) return prev;
        return [notification, ...prev];
      });
    });
    
    // Xử lý ping để giữ kết nối
    sse.addEventListener('ping', (event) => {
      console.log('Received ping from server');
    });
    
    // Xử lý sự kiện mặc định (nếu có)
    sse.onmessage = (event) => {
      console.log('Nhận sự kiện mặc định:', event.data);
    };
    
    // Xử lý lỗi
    sse.onerror = (error) => {
      console.error('Lỗi SSE:', error);
      setConnectionStatus('error');
    };
    
    // Dọn dẹp khi component unmount
    return () => {
      console.log('Đóng kết nối SSE...');
      if (sse) {
        sse.close();
        setConnectionStatus('disconnected');
      }
    };
  }, []);

  // Debug: Hiển thị thông báo trong console mỗi khi connectionStatus thay đổi
  useEffect(() => {
    console.log('Trạng thái kết nối:', connectionStatus);
  }, [connectionStatus]);

  // Đảm bảo context luôn có giá trị mới nhất
  const contextValue = {
    notifications,
    loading,
    connectionStatus,
    markAsRead: (notificationId) => {
      // Cập nhật trạng thái đã đọc cho thông báo
      setNotifications(notifications.map(n => 
        n._id === notificationId ? { ...n, status: 'read' } : n
      ));
    }
  };

  return (
    <NotificationContext.Provider value={contextValue}>
      {children}
      <ToastContainer />
    </NotificationContext.Provider>
  );
};

export default NotificationProvider;
