import React, { createContext, useState, useEffect, useContext } from 'react';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { getNotifications, markNotificationAsRead } from '../services/api';
import { io } from 'socket.io-client';

const NotificationContext = createContext();

export const useNotifications = () => useContext(NotificationContext);

export const NotificationProvider = ({ children }) => {
  const [socket, setSocket] = useState(null);
  const [notifications, setNotifications] = useState([]);
  const [loading, setLoading] = useState(true);
  const [socketStatus, setSocketStatus] = useState('disconnected');

  // Lấy thông báo ban đầu từ API
  useEffect(() => {
    const customerId = localStorage.getItem('customer_id');
    if (customerId) {
      const fetchNotifications = async () => {
        try {
          const response = await getNotifications();
          console.log('Dữ liệu thông báo đã tải:', response.data);
          setNotifications(response.data || []);
        } catch (error) {
          console.error('Lỗi khi lấy thông báo:', error);
        } finally {
          setLoading(false);
        }
      };
      fetchNotifications();
    } else {
      setLoading(false);
    }
  }, []);

  // Kết nối Socket.IO để nhận thông báo realtime
  useEffect(() => {
    console.log('Bắt đầu kết nối Socket.IO...');

    const customerId = localStorage.getItem('customer_id');
    if (!customerId) {
      console.log('Không tìm thấy customer_id, không thể kết nối Socket.IO');
      setSocketStatus('no customer_id');
      return;
    }

    // Kết nối đến Socket.IO server
    const socketUrl = 'http://localhost:8006';
    console.log('Kết nối Socket.IO đến:', socketUrl);

    const newSocket = io(socketUrl, {
      transports: ['polling', 'websocket'],  // Sử dụng polling trước, sau đó nâng cấp lên websocket
      reconnectionAttempts: 5,
      reconnectionDelay: 1000,
      timeout: 20000,
      path: '/',  // Đường dẫn mặc định
      forceNew: true
    });

    setSocket(newSocket);

    // Xử lý các sự kiện Socket.IO
    newSocket.on('connect', () => {
      console.log('Kết nối Socket.IO đã mở, ID:', newSocket.id);
      setSocketStatus('connected');

      // Tham gia room của khách hàng sau khi kết nối thành công
      console.log('Gửi yêu cầu join_room với customer_id:', customerId);
      newSocket.emit('join_room', { customer_id: customerId });
    });

    newSocket.on('connected', (data) => {
      console.log('Nhận sự kiện connected từ server:', data);
    });

    newSocket.on('disconnect', (reason) => {
      console.log('Socket.IO đã ngắt kết nối:', reason);
      setSocketStatus('disconnected');
    });

    newSocket.on('connect_error', (error) => {
      console.error('Lỗi kết nối Socket.IO:', error);
      setSocketStatus('error');
    });

    newSocket.on('joined', (data) => {
      console.log('Đã tham gia room:', data);
    });

    newSocket.on('unread_notifications', (data) => {
      console.log('Nhận thông báo chưa đọc:', data);
      // Cập nhật danh sách thông báo (nối thêm vào danh sách hiện tại)
      setNotifications(prev => {
        // Lọc ra các thông báo không trùng lặp
        const existingIds = new Set(prev.map(n => n._id));
        const newNotifications = data.filter(n => !existingIds.has(n._id));
        return [...prev, ...newNotifications];
      });
    });

    newSocket.on('notification', (data) => {
      console.log('Nhận thông báo mới:', data);

      // Hiển thị toast thông báo
      toast.success(data.content, {
        position: "top-right",
        autoClose: 5000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
      });

      // Cập nhật danh sách thông báo
      setNotifications(prev => [data, ...prev]);
    });

    newSocket.on('notification_marked_read', (data) => {
      console.log('Thông báo đã được đánh dấu đã đọc:', data);
      // Cập nhật trạng thái thông báo trong danh sách
      if (data.notification_id) {
        setNotifications(prev => prev.map(n => 
          n._id === data.notification_id ? { ...n, status: 'read' } : n
        ));
      }
    });

    newSocket.on('error', (error) => {
      console.error('Lỗi từ server Socket.IO:', error);
      toast.error(error.message || 'Lỗi không xác định từ server');
    });

    // Đóng kết nối khi component unmount
    return () => {
      console.log('Đóng kết nối Socket.IO');
      if (newSocket) {
        newSocket.off();
        newSocket.disconnect();
      }
    };
  }, [socketStatus === 'reconnecting']); // Thử kết nối lại khi trạng thái là reconnecting

  // Debug: Hiển thị thông báo trong console mỗi khi socketStatus thay đổi
  useEffect(() => {
    console.log('Trạng thái Socket:', socketStatus);
  }, [socketStatus]);

  // Đánh dấu thông báo đã đọc
  const handleMarkAsRead = async (notificationId) => {
    try {
      // Gọi API để đánh dấu đã đọc
      await markNotificationAsRead(notificationId);

      // Cập nhật trạng thái trong context
      setNotifications(notifications.map(n => 
        n._id === notificationId ? { ...n, status: 'read' } : n
      ));

      // Gửi lệnh đánh dấu đã đọc qua Socket.IO nếu kết nối đang mở
      if (socket && socket.connected) {
        socket.emit('mark_read', {
          notification_id: notificationId
        });
      }
    } catch (error) {
      console.error('Lỗi khi đánh dấu thông báo đã đọc:', error);
    }
  };

  // Đảm bảo context luôn có giá trị mới nhất
  const contextValue = {
    notifications,
    loading,
    socketStatus,
    markAsRead: handleMarkAsRead
  };

  return (
    <NotificationContext.Provider value={contextValue}>
      {children}
      <ToastContainer />
    </NotificationContext.Provider>
  );
};

export default NotificationProvider;
