import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import { getBookingById } from '../services/api';
import { ArrowLeftIcon, TicketIcon, ClockIcon, CurrencyDollarIcon, CalendarIcon, UserIcon, BellIcon } from '@heroicons/react/24/outline';
import { formatDistance } from 'date-fns';
import { vi } from 'date-fns/locale';
import { motion } from 'framer-motion';

const BookingDetail = () => {
  const { bookingId } = useParams();
  const [booking, setBooking] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchBookingDetails = async () => {
      try {
        const response = await getBookingById(bookingId);
        setBooking(response.data);
        setLoading(false);
      } catch (err) {
        setError('Không thể tải thông tin đặt vé');
        setLoading(false);
      }
    };

    fetchBookingDetails();
  }, [bookingId]);

  const formatTimeAgo = (date) => {
    try {
      return formatDistance(new Date(date), new Date(), {
        addSuffix: true,
        locale: vi
      });
    } catch (error) {
      return date;
    }
  };

  if (loading) return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-50 via-white to-purple-50">
      <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
    </div>
  );

  if (error) return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-50 via-white to-purple-50">
      <motion.div 
        initial={{ opacity: 0, scale: 0.95 }}
        animate={{ opacity: 1, scale: 1 }}
        className="text-center bg-white p-8 rounded-lg shadow-lg max-w-md mx-auto"
      >
        <svg className="w-16 h-16 mx-auto text-red-500 mb-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4m0 4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
        </svg>
        <p className="text-xl font-semibold mb-2">Đã có lỗi xảy ra</p>
        <p className="text-gray-600">{error}</p>
      </motion.div>
    </div>
  );

  if (!booking) return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-50 via-white to-purple-50">
      <motion.div 
        initial={{ opacity: 0, scale: 0.95 }}
        animate={{ opacity: 1, scale: 1 }}
        className="text-center bg-white p-8 rounded-lg shadow-lg max-w-md mx-auto"
      >
        <svg className="w-16 h-16 mx-auto text-gray-400 mb-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 11H5m14 0a2 2 0 012 2v6a2 2 0 01-2 2H5a2 2 0 01-2-2v-6a2 2 0 012-2m14 0V9a2 2 0 00-2-2M5 11V9a2 2 0 012-2m0 0V5a2 2 0 012-2h6a2 2 0 012 2v2M7 7h10" />
        </svg>
        <p className="text-xl font-semibold mb-2">Không tìm thấy thông tin đặt vé</p>
        <Link 
          to="/bookings" 
          className="text-blue-500 hover:text-blue-600 inline-flex items-center mt-4"
        >
          <ArrowLeftIcon className="w-5 h-5 mr-2" />
          Quay lại danh sách đặt vé
        </Link>
      </motion.div>
    </div>
  );

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-white to-purple-50">
      <div className="container mx-auto px-4 py-8">
        <motion.div 
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          className="max-w-4xl mx-auto"
        >
          <div className="flex items-center mb-8">
            <Link
              to="/bookings"
              className="flex items-center text-gray-600 hover:text-blue-600 transition-colors duration-200"
            >
              <ArrowLeftIcon className="w-5 h-5 mr-2" />
              Quay lại danh sách đặt vé
            </Link>
          </div>

          <div className="bg-white rounded-xl shadow-lg overflow-hidden">
            {/* Header */}
            <motion.div 
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              className="bg-gradient-to-r from-blue-600 to-blue-800 p-8 text-white relative overflow-hidden"
            >
              <div className="absolute inset-0 bg-black opacity-10"></div>
              <div className="relative z-10">
                <h1 className="text-3xl font-bold mb-3">{booking.movie_title}</h1>
                <div className="flex items-center space-x-4">
                  <span className={`
                    px-4 py-1.5 rounded-full text-sm font-medium
                    ${booking.status === 'paid' ? 'bg-green-500' :
                      booking.status === 'pending' ? 'bg-yellow-500' : 'bg-red-500'}
                    shadow-sm
                  `}>
                    {booking.status === 'paid' ? 'Đã thanh toán' :
                     booking.status === 'pending' ? 'Chờ thanh toán' :
                     booking.status.charAt(0).toUpperCase() + booking.status.slice(1)}
                  </span>
                  <span className="text-sm opacity-75 flex items-center">
                    <TicketIcon className="w-4 h-4 mr-1" />
                    Mã đặt vé: {booking._id}
                  </span>
                </div>
              </div>
            </motion.div>

            {/* Main Content */}
            <div className="p-8">
              <div className="grid md:grid-cols-2 gap-8">
                {/* Booking Details */}
                <motion.div 
                  initial={{ opacity: 0, x: -20 }}
                  animate={{ opacity: 1, x: 0 }}
                  transition={{ delay: 0.2 }}
                  className="space-y-6"
                >
                  <h2 className="text-xl font-semibold mb-6 flex items-center">
                    <TicketIcon className="w-6 h-6 mr-2 text-blue-500" />
                    Thông tin đặt vé
                  </h2>

                  <div className="space-y-6 bg-gray-50 rounded-lg p-6">
                    <div className="flex items-start space-x-4">
                      <ClockIcon className="w-6 h-6 text-blue-500 mt-1 flex-shrink-0" />
                      <div>
                        <p className="font-medium text-gray-700">Suất chiếu</p>
                        <p className="text-gray-600">
                          {new Date(booking.showtime).toLocaleString('vi-VN', {
                            weekday: 'long',
                            year: 'numeric',
                            month: 'long',
                            day: 'numeric',
                            hour: '2-digit',
                            minute: '2-digit'
                          })}
                        </p>
                      </div>
                    </div>

                    <div className="flex items-start space-x-4">
                      <TicketIcon className="w-6 h-6 text-blue-500 mt-1 flex-shrink-0" />
                      <div>
                        <p className="font-medium text-gray-700">Ghế đã chọn</p>
                        <p className="text-gray-600">
                          {booking.seats.map(seat => seat.seat_number).join(', ')}
                        </p>
                      </div>
                    </div>

                    <div className="flex items-start space-x-4">
                      <CurrencyDollarIcon className="w-6 h-6 text-blue-500 mt-1 flex-shrink-0" />
                      <div>
                        <p className="font-medium text-gray-700">Tổng tiền</p>
                        <p className="text-gray-600">
                          {booking.total_amount.toLocaleString()} VND
                        </p>
                      </div>
                    </div>

                    <div className="flex items-start space-x-4">
                      <CalendarIcon className="w-6 h-6 text-blue-500 mt-1 flex-shrink-0" />
                      <div>
                        <p className="font-medium text-gray-700">Thời gian đặt vé</p>
                        <p className="text-gray-600">
                          {formatTimeAgo(booking.created_at)}
                        </p>
                      </div>
                    </div>
                  </div>
                </motion.div>

                {/* Payment Information */}
                {booking.payment && (
                  <motion.div 
                    initial={{ opacity: 0, x: 20 }}
                    animate={{ opacity: 1, x: 0 }}
                    transition={{ delay: 0.3 }}
                    className="space-y-6"
                  >
                    <h2 className="text-xl font-semibold mb-6 flex items-center">
                      <CurrencyDollarIcon className="w-6 h-6 mr-2 text-blue-500" />
                      Thông tin thanh toán
                    </h2>

                    <div className="bg-gray-50 rounded-lg p-6 space-y-4">
                      <div>
                        <p className="font-medium text-gray-700">Mã thanh toán</p>
                        <p className="text-gray-600 mt-1">{booking.payment._id}</p>
                      </div>

                      <div>
                        <p className="font-medium text-gray-700">Phương thức thanh toán</p>
                        <p className="text-gray-600 mt-1">{booking.payment.payment_method}</p>
                      </div>

                      <div>
                        <p className="font-medium text-gray-700">Trạng thái</p>
                        <span className={`
                          inline-block px-4 py-1.5 rounded-full text-sm font-medium mt-2
                          ${booking.payment.status === 'completed' ? 'bg-green-100 text-green-800' :
                            booking.payment.status === 'pending' ? 'bg-yellow-100 text-yellow-800' :
                            'bg-red-100 text-red-800'}
                        `}>
                          {booking.payment.status === 'completed' ? 'Hoàn thành' :
                           booking.payment.status === 'pending' ? 'Đang xử lý' :
                           booking.payment.status.charAt(0).toUpperCase() + booking.payment.status.slice(1)}
                        </span>
                      </div>

                      {booking.payment.paid_at && (
                        <div>
                          <p className="font-medium text-gray-700">Thời gian thanh toán</p>
                          <p className="text-gray-600 mt-1">
                            {formatTimeAgo(booking.payment.paid_at)}
                          </p>
                        </div>
                      )}
                    </div>
                  </motion.div>
                )}
              </div>

              {/* Notifications */}
              {booking.notifications && booking.notifications.length > 0 && (
                <motion.div 
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ delay: 0.4 }}
                  className="mt-8"
                >
                  <h2 className="text-xl font-semibold mb-6 flex items-center">
                    <BellIcon className="w-6 h-6 mr-2 text-blue-500" />
                    Lịch sử thông báo
                  </h2>
                  <div className="space-y-4 bg-gray-50 rounded-lg p-6">
                    {booking.notifications.map((notification, index) => (
                      <motion.div
                        key={notification._id}
                        initial={{ opacity: 0, x: -20 }}
                        animate={{ opacity: 1, x: 0 }}
                        transition={{ delay: 0.1 * index }}
                        className="flex items-start space-x-4 p-4 bg-white rounded-lg shadow-sm"
                      >
                        <div className="flex-shrink-0">
                          <BellIcon className="w-6 h-6 text-blue-500" />
                        </div>
                        <div className="flex-1">
                          <p className="text-gray-700">{notification.content}</p>
                          <p className="text-sm text-gray-500 mt-1">
                            {formatTimeAgo(notification.created_at)}
                          </p>
                        </div>
                      </motion.div>
                    ))}
                  </div>
                </motion.div>
              )}
            </div>
          </div>
        </motion.div>
      </div>
    </div>
  );
};

export default BookingDetail;
