import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { getBookings } from '../services/api';
import { motion } from 'framer-motion';
import { CalendarIcon, CreditCardIcon, ClockIcon } from '@heroicons/react/24/outline';

const Bookings = () => {
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [activeTab, setActiveTab] = useState('all');

  useEffect(() => {
    const fetchBookings = async () => {
      try {
        const response = await getBookings();
        // Sắp xếp booking theo thời gian mới nhất
        const sortedBookings = response.data.sort((a, b) => 
          new Date(b.showtime) - new Date(a.showtime)
        );
        setBookings(sortedBookings);
        setLoading(false);
      } catch (err) {
        if (err.message === 'Customer ID not found') {
          setError('Bạn cần đăng nhập để xem thông tin đặt vé');
        } else {
          setError('Không thể tải thông tin đặt vé');
        }
        setLoading(false);
      }
    };

    fetchBookings();
  }, []);

  // Lọc booking theo tab đang active
  const filteredBookings = bookings.filter(booking => {
    if (activeTab === 'all') return true;
    if (activeTab === 'paid') return booking.status === 'paid';
    if (activeTab === 'pending') return booking.status === 'pending';
    return true;
  });

  if (loading) return (
    <div className="flex items-center justify-center min-h-screen">
      <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
    </div>
  );
  
  if (error) return (
    <div className="flex items-center justify-center min-h-screen">
      <div className="text-center text-red-500 p-8 bg-red-50 rounded-lg shadow-sm">
        <svg className="w-12 h-12 mx-auto text-red-500 mb-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4m0 4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
        </svg>
        {error}
      </div>
    </div>
  );

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-white to-purple-50">
      <div className="container mx-auto px-4 py-8">
        <motion.h1 
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          className="text-4xl font-bold mb-8 text-gray-800 text-center"
        >
          Đặt vé của tôi
        </motion.h1>

        <motion.div 
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          className="flex space-x-1 mb-6 bg-white p-1 rounded-lg shadow-sm w-fit mx-auto"
        >
          <button
            onClick={() => setActiveTab('all')}
            className={`px-6 py-2 rounded-md transition-all duration-200 flex items-center space-x-2 ${
              activeTab === 'all'
                ? 'bg-gradient-to-r from-blue-500 to-blue-600 text-white shadow-md'
                : 'text-gray-600 hover:bg-gray-50'
            }`}
          >
            <CalendarIcon className="w-5 h-5" />
            <span>Tất cả ({bookings.length})</span>
          </button>
          <button
            onClick={() => setActiveTab('paid')}
            className={`px-6 py-2 rounded-md transition-all duration-200 flex items-center space-x-2 ${
              activeTab === 'paid'
                ? 'bg-gradient-to-r from-green-500 to-green-600 text-white shadow-md'
                : 'text-gray-600 hover:bg-gray-50'
            }`}
          >
            <CreditCardIcon className="w-5 h-5" />
            <span>Đã thanh toán ({bookings.filter(b => b.status === 'paid').length})</span>
          </button>
          <button
            onClick={() => setActiveTab('pending')}
            className={`px-6 py-2 rounded-md transition-all duration-200 flex items-center space-x-2 ${
              activeTab === 'pending'
                ? 'bg-gradient-to-r from-yellow-500 to-yellow-600 text-white shadow-md'
                : 'text-gray-600 hover:bg-gray-50'
            }`}
          >
            <ClockIcon className="w-5 h-5" />
            <span>Chờ thanh toán ({bookings.filter(b => b.status === 'pending').length})</span>
          </button>
        </motion.div>

        {filteredBookings.length === 0 ? (
          <motion.div 
            initial={{ opacity: 0, scale: 0.95 }}
            animate={{ opacity: 1, scale: 1 }}
            className="text-center p-8 bg-white rounded-lg shadow-sm max-w-md mx-auto"
          >
            <svg className="w-16 h-16 mx-auto text-gray-400 mb-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 11H5m14 0a2 2 0 012 2v6a2 2 0 01-2 2H5a2 2 0 01-2-2v-6a2 2 0 012-2m14 0V9a2 2 0 00-2-2M5 11V9a2 2 0 012-2m0 0V5a2 2 0 012-2h6a2 2 0 012 2v2M7 7h10" />
            </svg>
            <p className="text-gray-500 mb-4">Không tìm thấy đơn đặt vé nào</p>
            <Link
              to="/"
              className="inline-flex items-center px-4 py-2 bg-blue-500 hover:bg-blue-600 text-white rounded-md transition-colors duration-200"
            >
              <svg className="w-5 h-5 mr-2" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
              </svg>
              Đặt vé xem phim
            </Link>
          </motion.div>
        ) : (
          <div className="grid gap-6 md:grid-cols-2 lg:grid-cols-3">
            {filteredBookings.map((booking, index) => (
              <motion.div
                key={booking._id}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: index * 0.1 }}
                className="bg-white rounded-lg shadow-sm hover:shadow-lg transition-all duration-200 overflow-hidden"
              >
                <div className="p-6">
                  <div className="flex justify-between items-start mb-4">
                    <h2 className="text-xl font-semibold text-gray-800 line-clamp-2">
                      {booking.movie_title}
                    </h2>
                    <span
                      className={`
                        px-3 py-1 rounded-full text-sm font-medium
                        ${booking.status === 'paid'
                          ? 'bg-gradient-to-r from-green-500 to-green-600 text-white'
                          : booking.status === 'pending'
                            ? 'bg-gradient-to-r from-yellow-500 to-yellow-600 text-white'
                            : 'bg-gradient-to-r from-red-500 to-red-600 text-white'
                        }
                      `}
                    >
                      {booking.status === 'paid' ? 'Đã thanh toán' : 
                       booking.status === 'pending' ? 'Chờ thanh toán' : 
                       booking.status.charAt(0).toUpperCase() + booking.status.slice(1)}
                    </span>
                  </div>

                  <div className="space-y-2 mb-4">
                    <p className="text-gray-600 flex items-center">
                      <CalendarIcon className="w-5 h-5 mr-2 text-gray-400" />
                      {booking.showtime && booking.showtime !== 'Invalid Date' 
                        ? new Date(booking.showtime).toLocaleString('vi-VN') 
                        : 'Không có thông tin'}
                    </p>
                    <p className="text-gray-600 flex items-center">
                      <svg className="w-5 h-5 mr-2 text-gray-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 21V5a2 2 0 00-2-2H7a2 2 0 00-2 2v16m14 0h2m-2 0h-5m-9 0H3m2 0h5M9 7h1m-1 4h1m4-4h1m-1 4h1m-5 10v-5a1 1 0 011-1h2a1 1 0 011 1v5m-4 0h4" />
                      </svg>
                      Ghế: {Array.isArray(booking.seats)
                        ? booking.seats.map(seat => typeof seat === 'object' ? seat.seat_number : seat).join(', ')
                        : booking.seats
                      }
                    </p>
                    <p className="text-gray-600 flex items-center">
                      <CreditCardIcon className="w-5 h-5 mr-2 text-gray-400" />
                      {booking.total_amount.toLocaleString()} VND
                    </p>
                  </div>

                  <div className="flex justify-end pt-4 border-t">
                    {booking.status === 'pending' ? (
                      <Link
                        to={`/payment/${booking._id}`}
                        className="inline-flex items-center px-4 py-2 bg-gradient-to-r from-blue-500 to-blue-600 text-white rounded-md hover:from-blue-600 hover:to-blue-700 transition-all duration-200"
                      >
                        <CreditCardIcon className="w-5 h-5 mr-2" />
                        Thanh toán ngay
                      </Link>
                    ) : (
                      <Link
                        to={`/bookings/${booking._id}`}
                        className="inline-flex items-center text-blue-500 hover:text-blue-600 transition-colors duration-200"
                      >
                        <svg className="w-5 h-5 mr-1" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 12a3 3 0 11-6 0 3 3 0 016 0z" />
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M2.458 12C3.732 7.943 7.523 5 12 5c4.478 0 8.268 2.943 9.542 7-1.274 4.057-5.064 7-9.542 7-4.477 0-8.268-2.943-9.542-7z" />
                        </svg>
                        Xem chi tiết
                      </Link>
                    )}
                  </div>
                </div>
              </motion.div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default Bookings;
