import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { getSeats, bookSeats, createBooking, createPayment } from '../services/api';

const SeatSelection = () => {
  const { showtimeId } = useParams();
  const navigate = useNavigate();
  const [seats, setSeats] = useState([]);
  const [selectedSeats, setSelectedSeats] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showtimeInfo, setShowtimeInfo] = useState(null);

  // Lấy danh sách ghế
  useEffect(() => {
    const fetchSeats = async () => {
      try {
        const response = await getSeats(showtimeId);
        setSeats(response.data);
        console.log('Ghế:', response.data);
        setLoading(false);
      } catch (err) {
        console.error('Lỗi khi lấy dữ liệu ghế:', err);
        setError('Không thể tải thông tin ghế');
        setLoading(false);
      }
    };

    fetchSeats();
  }, [showtimeId]);

  // Xử lý chọn ghế
  const handleSeatClick = (seat) => {
    if (seat.status === 'booked') return;

    setSelectedSeats((prev) => {
      if (prev.includes(seat.seat_number)) {
        return prev.filter((s) => s !== seat.seat_number);
      }
      return [...prev, seat.seat_number];
    });
  };

  // Tiến hành đặt vé
  const handleBooking = async () => {
    if (selectedSeats.length === 0) {
      setError('Vui lòng chọn ít nhất một ghế');
      return;
    }

    try {
      // Đặt ghế
      await bookSeats(showtimeId, selectedSeats);

      // Tạo đơn đặt vé
      const bookingData = {
        showtime_id: showtimeId,
        seats: selectedSeats,
        total_amount: selectedSeats.length * 2000, // Giả sử giá mỗi ghế là 2000
        status: 'pending'
      };
      const bookingResponse = await createBooking(bookingData);

      // Tạo giao dịch thanh toán
      const paymentData = {
        booking_id: bookingResponse.data.id,
        amount: bookingResponse.data.total_amount,
        payment_method: 'credit_card',
        status: 'pending'
      };
      await createPayment(paymentData);

      // Chuyển đến trang xác nhận đặt vé
      navigate(`/bookings/${bookingResponse.data.id}`);
    } catch (err) {
      console.error('Lỗi khi đặt vé:', err);
      setError('Không thể hoàn tất đặt vé');
    }
  };

  if (loading) return <div className="text-center p-8">Đang tải...</div>;
  if (error) return <div className="text-center text-red-500 p-8">{error}</div>;

  // Tạo bố cục ghế trong rạp
  const renderSeatMap = () => {
    // Nhóm ghế theo hàng (A, B, C, ...)
    const seatsByRow = seats.reduce((acc, seat) => {
      const row = seat.seat_number.charAt(0);
      if (!acc[row]) acc[row] = [];
      acc[row].push(seat);
      return acc;
    }, {});

    // Sắp xếp các hàng theo thứ tự
    return Object.keys(seatsByRow).sort().map(row => (
      <div key={row} className="flex mb-2 items-center">
        <div className="w-8 text-center font-bold">{row}</div>
        <div className="flex flex-1 gap-2 justify-center">
          {seatsByRow[row].sort((a, b) => {
            const numA = parseInt(a.seat_number.substring(1));
            const numB = parseInt(b.seat_number.substring(1));
            return numA - numB;
          }).map(seat => (
            <button
              key={seat._id}
              onClick={() => handleSeatClick(seat)}
              disabled={seat.status === 'booked'}
              className={`
                w-10 h-10 flex items-center justify-center rounded
                ${seat.status === 'booked' 
                  ? 'bg-gray-400 cursor-not-allowed' 
                  : selectedSeats.includes(seat.seat_number)
                    ? 'bg-blue-500 text-white'
                    : 'bg-gray-200 hover:bg-gray-300'
                }
              `}
            >
              {seat.seat_number.substring(1)}
            </button>
          ))}
        </div>
      </div>
    ));
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-2xl font-bold mb-6 text-center">Chọn ghế</h1>
      
      <div className="bg-white rounded-lg shadow-md p-6 mb-6">
        <div className="w-full bg-gray-800 h-2 rounded-lg mb-8"></div>
        <div className="text-center mb-2 text-gray-500">Màn hình</div>
        
        <div className="mt-8">
          {renderSeatMap()}
        </div>
      </div>

      <div className="bg-white rounded-lg shadow-md p-6">
        <h2 className="text-xl font-semibold mb-4">Thông tin đặt vé</h2>
        
        <div className="mb-4">
          <div className="flex justify-between mb-2">
            <span>Ghế đã chọn:</span>
            <span className="font-medium">
              {selectedSeats.length > 0 ? selectedSeats.join(', ') : 'Chưa chọn ghế'}
            </span>
          </div>
          <div className="flex justify-between">
            <span>Số lượng:</span>
            <span className="font-medium">{selectedSeats.length}</span>
          </div>
          <div className="flex justify-between mt-2">
            <span>Tổng tiền:</span>
            <span className="font-medium text-blue-600">
              {new Intl.NumberFormat('vi-VN', {
                style: 'currency',
                currency: 'VND'
              }).format(selectedSeats.length * 2000)}
            </span>
          </div>
        </div>
        
        <div className="flex gap-2 mt-6">
          <button
            onClick={() => navigate(-1)}
            className="flex-1 py-2 px-4 bg-gray-300 hover:bg-gray-400 rounded"
          >
            Quay lại
          </button>
          <button
            onClick={handleBooking}
            disabled={selectedSeats.length === 0}
            className={`
              flex-1 py-2 px-4 rounded text-white
              ${selectedSeats.length === 0
                ? 'bg-gray-400 cursor-not-allowed'
                : 'bg-blue-500 hover:bg-blue-600'
              }
            `}
          >
            Tiến hành đặt vé
          </button>
        </div>
      </div>
      
      <div className="mt-6 flex justify-center gap-6">
        <div className="flex items-center">
          <div className="w-6 h-6 bg-gray-200 rounded mr-2"></div>
          <span>Ghế trống</span>
        </div>
        <div className="flex items-center">
          <div className="w-6 h-6 bg-gray-400 rounded mr-2"></div>
          <span>Đã đặt</span>
        </div>
        <div className="flex items-center">
          <div className="w-6 h-6 bg-blue-500 rounded mr-2"></div>
          <span>Đang chọn</span>
        </div>
      </div>
    </div>
  );
};

export default SeatSelection;
